#import "TRStateTransition.h"

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
@interface TRStateTransition ()

@property (nonatomic, copy) NSString *identifier;

@end

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
@implementation TRStateTransition

#pragma mark - Dealloc and Initialization

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
+ (instancetype)transitionWithIdentifier:(NSString *)identifier
{
    return [[[self class] alloc] initWithIdentifier:identifier];
}

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
- (instancetype)initWithIdentifier:(NSString *)identifier
{
    if((self = [super init])) {
        self.identifier = identifier;
    }
    return self;
}

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
- (id)copyWithZone:(NSZone *)zone
{
    return [[self class] transitionWithIdentifier:self.identifier];
}

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
- (NSUInteger)hash
{
    return [self.identifier hash];
}

////////////////////////////////////////////////////////////////////////////////
////////////////////////////////////////////////////////////////////////////////
- (BOOL)isEqual:(id)object
{
    if(self == object) return YES;
    if([object isKindOfClass:[self class]] == NO) return NO;
    
    return [((TRStateTransition *)object).identifier isEqualToString:self.identifier];
}

@end
